import React, { useState, useEffect } from 'react';
import './Depoimentos.css';
import Carousel from 'react-bootstrap/Carousel';
import Card from 'react-bootstrap/Card';

const url = "https://maps.googleapis.com/maps/api/place/details/json?place_id=ChIJET2xk12dGZURptjFws5206A&fields=reviews&key=AIzaSyBpLFsCQoIjG9AFULiHY5107lAW8xF_6qo&language=pt-BR";

const Depoimentos = () => {
  const [depoimentos, setDepoimentos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/')
      .then((response) => response.json())
      .then((data) => setDepoimentos(data.result.reviews))
      .catch((error) => console.error('Erro na solicitaÃ§Ã£o:', error));
  }, []);

  return (
    <div>
      <div className="titulo">
        <h2>ðŸ“œ Depoimentos</h2>
      </div>

      <Carousel>
        {depoimentos.map((depoimento, index) => (
          <Carousel.Item key={index}>
            <Card>
              <div className="cardDepo">
                <div className="head">
                  <img src={depoimento.profile_photo_url} width={40} height={40} alt="Profile" />
                  <p><strong>{depoimento.author_name}</strong></p>
                </div>

                <div className="nota">
                  {Array.from({ length: 5 }).map((_, notaIndex) => (
                    <img
                      key={notaIndex}
                      src={`/ico/estrela${notaIndex < depoimento.rating ? '' : 'Branca'}.png`}
                      width={25}
                      height={25}
                      alt={`Estrela ${notaIndex + 1}`}
                    />
                  ))}
                </div>

                <div className="body">
                  <span>{depoimento.text}</span>
                </div>
              </div>
            </Card>
          </Carousel.Item>
        ))}
      </Carousel>
    </div>
  );
};

export default Depoimentos;
