import './Contato.css'
import { useState, useEffect } from 'react';



const Contato = () => {

  const [text, setText] = useState('');
  const originalText = 'Entre em contato, sou bem de boas! ðŸ˜¸';
  const typingSpeed = 200; // Velocidade da digitaÃ§Ã£o (em milissegundos por caractere)

  useEffect(() => {
    let currentIndex = 0;
    const timer = setInterval(() => {
      if (currentIndex < originalText.length) {
        setText(originalText.slice(0, currentIndex + 1));
        currentIndex++;
      } else {
        clearInterval(timer);
      }
    }, typingSpeed);

    return () => {
      clearInterval(timer);
    };
  }, []);


  return (

    <div className="contato">
        <div className="supeiorCont">
