import React from 'react'
import './Contato.css'
import { useState, useEffect } from 'react';



const Contato = () => {

  const originalText = 'Sinta-se Ã  vontade para me chamar em qualquer hoara! ðŸ˜¸';
  const typingSpeed = 50; // Velocidade da digitaÃ§Ã£o (em milissegundos por caractere)

  const [text, setText] = useState('');
  const [showCursor, setShowCursor] = useState(true);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    if (currentIndex < originalText.length) {
      const timer = setTimeout(() => {
        setText(originalText.slice(0, currentIndex + 1));
        setCurrentIndex(currentIndex + 1);
      }, typingSpeed);

      return () => {
        clearTimeout(timer);
      };
    }
  }, [currentIndex]);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setShowCursor((prevState) => !prevState);
    }, 500); // Alterna o cursor a cada 500ms

    return () => {
      clearInterval(cursorInterval);
    };
  }, []);

  return (

    <div className="contato">
        <div className="supeiorCont">
                <div className="titulo">
                    <h2>ðŸ“± Contato</h2>
                </div>

                <div className="containerContato">
                  <div className="containerFilho1">
                  <p className="contato-text">{text}{'|'}</p>


                  </div>

                  <div className="containerFilho2">
                    <div className="rede">
                        <img src="./ico/email.png" width={'48px'} title="JavaScript"/>
                        <p> contato@gabrielaramires.com.br</p>
                      </div>
                      <div className="rede">
                        <img src="./ico/whats.png" width={'48px'} title="JavaScript"/>
                        <p> (51) 98018-7026</p>
                      </div>
                      <div className="rede">
                        <img src="./ico/github.png" width={'48px'} title="JavaScript"/>
                        <p> github.com/gabi-ramires</p>
                      </div>
                      <div className="rede">
                        <img src="./ico/link.png" width={'48px'} title="JavaScript"/>
                        <p> linkedin.com/in/gabi-ramires</p>
                      </div>
                    </div>
                </div>
                <div className="footer">
                  <p>Â© 2023 Gabriela Ramires. All rights reserved.</p>
                </div>
        </div>

    </div>
  )
}

function CursorBlink({ showCursor }) {
  return <p className="cursor">{showCursor && '|'}</p>;
}

export default Contato
